mapper.select:
SELECT * FROM audit_logs


        AND user_id = #{userId}


        AND username LIKE CONCAT('%', #{username}, '%')


        AND operation_type = #{operationType}


        AND status = #{status}


        AND created_at >= #{startTime}


        AND created_at <= #{endTime}


ORDER BY created_at DESC

    LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM audit_logs


        AND user_id = #{userId}


        AND username LIKE CONCAT('%', #{username}, '%')


        AND operation_type = #{operationType}


        AND status = #{status}


        AND created_at >= #{startTime}


        AND created_at <= #{endTime}
